mapper.select:
SELECT
	COUNT(*)
FROM
	${ tb_name }

	
		idx = #{ idx }
	
	
		AND tb_idx = #{ tb_idx }
	
	
		writer_key = #{ writer_key }
	
	
		writer_name LIKE CONCAT('%' , #{ writer_name } , '%')
	
	
		content_text LIKE CONCAT('%' , #{ content_text } , '%')

SELECT
	*
FROM
	${ tb_name }

	
		idx = #{ idx }
	
	
		AND tb_idx = #{ tb_idx }
	
	
		writer_key = #{ writer_key }
	
	
		writer_name LIKE CONCAT('%' , #{ writer_name } , '%')
	
	
		content_text LIKE CONCAT('%' , #{ content_text } , '%')
	

ORDER BY
	pidx DESC, tree_level ASC, regdate ASC

mapper.update:
UPDATE
	${ tb_name }
SET
	is_use = #{ is_use }
WHERE
	idx = #{ idx }

mapper.delete:
DELETE FROM
	${ tb_name }
WHERE
	idx = #{ idx }
