mapper.select:
		SELECT
			COUNT(*)
		FROM
		${ tb_name }
		
			
				idx = #{ idx }
			
			
				writer_key = #{ writer_key }
			
			
				writer LIKE CONCAT('%' , #{ writer } , '%')
			
			
				title LIKE CONCAT('%' , #{ title } , '%')
			
			
				content_text LIKE CONCAT('%' , #{ content_text } , '%')
			
			
				AND is_use = #{ is_use }
			
			
				AND is_notice = #{ is_notice }
			

			
				AND (idx = pidx)

SELECT
A.*,
A.idx, A.pidx, A.tree_level, A.writer_key, A.writer, A.title, A.is_use, A.is_notice, A.reg_ip, A.views, A.opt1, A.opt2, A.regdate,
( SELECT COUNT(*) FROM ${ tb_name }_comment WHERE tb_idx = A.idx ) AS comment_count FROM ${ tb_name } AS A
idx = #{ idx }
AND pidx = #{ pidx } AND ( idx <> pidx )
AND writer_key = #{ writer_key }
AND writer LIKE CONCAT('%' , #{ writer } , '%')
AND title LIKE CONCAT('%' , #{ title } , '%')
AND content_text LIKE CONCAT('%' , #{ content_text } , '%')
AND is_use = #{ is_use }
AND is_notice = #{ is_notice }
AND (idx = pidx)
ORDER BY pidx DESC, tree_level ASC, regdate ASC
LIMIT #{ start }, #{ page_count }

mapper.update:
UPDATE
	${ tb_name }
SET
	is_use = #{ is_use }
WHERE
	idx = #{ idx }

mapper.delete:
DELETE FROM
	${ tb_name }
WHERE
	idx = #{ idx }
