mapper.select:
SELECT
	COUNT(A.idx)
FROM 
	tb_users AS A

	
		A.idx = #{ idx }
	
	
		A.user_id = #{ user_id }
	
	
		A.user_id = #{ user_id }
		
			AND A.passwd = MD5( #{ passwd })
		
	
	
		AND A.user_id LIKE CONCAT( '%', #{ searchValue }, '%' ) OR A.user_name LIKE CONCAT( '%', #{ searchValue }, '%' ) 
	

ORDER BY A.REGDATE DESC

 		SELECT
 			*
 		FROM
			tb_users AS A
 		
 			
 				A.idx = #{ idx }
 			
 			
 				A.user_id = #{ user_id }
 			
 			
 				A.user_id = #{ user_id }
 				
 					AND A.passwd = MD5( #{ passwd })
 				
 			
 			
 				AND A.user_id LIKE CONCAT( '%', #{ searchValue }, '%' ) OR A.user_name LIKE CONCAT( '%', #{ searchValue }, '%' ) 
 			
 		
 		
		
 			ORDER BY
				A.${ ordering } ${ sort }
 		
 		
 			ORDER BY
				A.regdate DESC
 		 		
 		
 		
 			LIMIT #{start}, #{limit}
