			,link_position_y = #{link_position_y}
		
		
			,link_size_x = #{link_size_x}
		
		
			,link_size_y = #{link_size_y}
		
		
			,close_position_x = #{close_position_x}
		
		
			,close_position_y = #{close_position_y}
		
		
			,size_x = #{size_x}
		
		
			,size_y = #{size_y}
		

		
			,regdate = #{regdate}
		
		
			,url = #{url}
		
		
			,is_use = #{is_use}
		

		WHERE
			idx = #{ idx }
