mapper.select:
SELECT
	COUNT(*)
FROM
	tb_files


		idx = #{ idx }


		AND tb_idx = #{ tb_idx }


		AND tb_name = #{ tb_name }

SELECT
	*
FROM
	tb_files


		idx = #{ idx }


		AND tb_idx = #{ tb_idx }


		AND tb_name = #{ tb_name }


ORDER BY
	idx ASC

mapper.update:
UPDATE
	tb_files
SET

	tb_idx = #{ tb_idx }


	, tb_name = #{ tb_name }


	, web_path = #{ web_path }


	, realfilename = #{ realfilename }


	, filename = #{ filename }


	, fileext = #{ fileext }


	, filesize = #{ filesize }


	, regdate = #{ regdate }



		idx = #{ idx }
